import React, { useState, useEffect } from 'react'; // React hooks
import CalendarModal from '../components/CalendarModal'; // Calendar Modal
import MyBookingSidebar from '../components/MyBookingSidebar'; // Booking Sidebar
import { fetchGowns } from '../services/api'; // API call to fetch gowns
import './Collection.css'; // CSS styles for Collection page

const Collection = () => {
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [dateRange, setDateRange] = useState({
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    });
    const [gowns, setGowns] = useState([]);
    const [cartItems, setCartItems] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetchGowns();
                setGowns(response.data);
            } catch (error) {
                console.error('Error fetching gowns:', error);
            }
        };
        fetchData();
    }, []);

    const handleDateChange = (ranges) => {
        const { selection } = ranges;
        setDateRange(selection);
        setCalendarOpen(false);
    };

    const addToCart = (gown) => {
        setCartItems([...cartItems, { ...gown, dateRange }]);
    };

    const filteredGowns = gowns.filter((gown) =>
        gown.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1 className="collection-title">Dresses for Every Event</h1>
            </header>
            <div className="filter-section">
                <button
                    onClick={() => setCalendarOpen(true)}
                    className="date-button"
                >
                    {dateRange.startDate.toLocaleDateString()} -{' '}
                    {dateRange.endDate.toLocaleDateString()}
                </button>
                <input
                    type="text"
                    placeholder="Search gowns..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                />
            </div>
            <div className="dress-grid">
                {filteredGowns.map((gown) => (
                    <div key={gown._id} className="dress-card">
                        <img src={`/assets/${gown.image}`} alt={gown.name} />
                        <h3>{gown.name}</h3>
                        <button onClick={() => addToCart(gown)}>
                            Add to Cart
                        </button>
                    </div>
                ))}
            </div>
            {calendarOpen && (
                <CalendarModal
                    dateRange={{ selection: dateRange }}
                    onDateChange={handleDateChange}
                    onClose={() => setCalendarOpen(false)}
                />
            )}
            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
};

export default Collection;
