import React from 'react';
import './GownDetailsModal.css';

const GownDetailsModal = ({ gown, onClose, onAddToCart }) => {
    return (
        <div className="gown-modal">
            <div className="modal-content">
                <button className="close-button" onClick={onClose}>X</button>
                <img src={`/assets/${gown.image}`} alt={gown.name} className="gown-image" />
                <h3>{gown.name}</h3>
                <p>{gown.description}</p>
                <p>Price: ${gown.price}</p>
                <button className="add-to-cart" onClick={() => onAddToCart(gown)}>
                    Add to Cart
                </button>
            </div>
        </div>
    );
};

export default GownDetailsModal;
