import React, { useState, useEffect } from "react";
import axios from "../utils/api";

const Collection = () => {
  const [gowns, setGowns] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const fetchGowns = async () => {
      try {
        const response = await axios.get("/gowns");
        setGowns(response.data);
      } catch (error) {
        console.error("Error fetching gowns:", error);
      }
    };
    fetchGowns();
  }, []);

  const filteredGowns = gowns.filter((gown) =>
    gown.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h1>Collection</h1>
      <div>
        <input
          type="text"
          placeholder="Search gowns..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>
      <div>
        {filteredGowns.map((gown) => (
          <div key={gown._id}>
            <h3>{gown.name}</h3>
            <p>Price: ${gown.price}</p>
            <img src={gown.image} alt={gown.name} style={{ width: "200px" }} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Collection;
