import React, { useState, useEffect } from "react";
import axios from "../utils/api"; // Adjust the path if needed
import { DateRange } from "react-date-range"; // Importing the date range picker
import "react-date-range/dist/styles.css"; // Main styles
import "react-date-range/dist/theme/default.css"; // Theme styles
import "./Collection.css"; // Optional: for styling

const Collection = () => {
  const [gowns, setGowns] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedGown, setSelectedGown] = useState(null); // Gown selected for rental
  const [rentalDates, setRentalDates] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  const [availabilityMessage, setAvailabilityMessage] = useState("");

  useEffect(() => {
    const fetchGowns = async () => {
      try {
        const response = await axios.get("/gowns");
        setGowns(response.data);
      } catch (error) {
        console.error("Error fetching gowns:", error);
      }
    };
    fetchGowns();
  }, []);

  // Filter gowns based on search query
  const filteredGowns = gowns.filter((gown) =>
    gown.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle checking gown availability
  const handleCheckAvailability = async (gownId) => {
    try {
      const response = await axios.post(`/gowns/${gownId}/check-availability`, {
        startDate: rentalDates[0].startDate,
        endDate: rentalDates[0].endDate,
      });

      if (response.data.available) {
        setAvailabilityMessage("The gown is available for the selected dates.");
      } else {
        setAvailabilityMessage("The gown is not available for the selected dates.");
      }
    } catch (error) {
      console.error("Error checking availability:", error);
      setAvailabilityMessage("An error occurred while checking availability.");
    }
  };

  return (
    <div className="collection">
      <h1>Our Collection</h1>
      <input
        type="text"
        placeholder="Search gowns..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-bar"
      />
      <div className="gown-list">
        {filteredGowns.length > 0 ? (
          filteredGowns.map((gown) => (
            <div key={gown._id} className="gown-card">
              <img src={gown.image} alt={gown.name} className="gown-image" />
              <h2>{gown.name}</h2>
              <p>Category: {gown.category}</p>
              <p>Price: ${gown.price}</p>
              <button
                onClick={() => setSelectedGown(gown)}
                className="availability-button"
              >
                Check Availability
              </button>
            </div>
          ))
        ) : (
          <p>No gowns found.</p>
        )}
      </div>

      {/* Modal or section for date selection */}
      {selectedGown && (
        <div className="availability-modal">
          <h2>Check Availability for {selectedGown.name}</h2>
          <DateRange
            editableDateInputs={true}
            onChange={(item) => setRentalDates([item.selection])}
            moveRangeOnFirstSelection={false}
            ranges={rentalDates}
          />
          <button
            onClick={() => handleCheckAvailability(selectedGown._id)}
            className="check-availability-button"
          >
            Confirm Dates
          </button>
          <p className="availability-message">{availabilityMessage}</p>
          <button
            onClick={() => setSelectedGown(null)}
            className="close-modal-button"
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default Collection;
