// DatePicker.js
import React, { useState } from 'react';
import { DateRange } from 'react-date-range';
import 'react-date-range/dist/styles.css'; // main style file
import 'react-date-range/dist/theme/default.css'; // theme css file
import './DatePicker.css';

function DatePicker({ onDateChange }) {
    const [state, setState] = useState([
        {
            startDate: new Date(),
            endDate: null,
            key: 'selection',
        },
    ]);
    const [isOpen, setIsOpen] = useState(false);

    const handleSelect = (ranges) => {
        setState([ranges.selection]);
        if (onDateChange) {
            onDateChange(ranges.selection);
        }
    };

    return (
        <div className="date-picker-container">
            <button className="date-picker-button" onClick={() => setIsOpen(!isOpen)}>
                {state[0].startDate ? state[0].startDate.toLocaleDateString() : 'Pickup Date'} - {state[0].endDate ? state[0].endDate.toLocaleDateString() : 'Return Date'}
            </button>
            {isOpen && (
                <div className="date-picker-popup">
                    <DateRange
                        editableDateInputs={true}
                        onChange={handleSelect}
                        moveRangeOnFirstSelection={false}
                        ranges={state}
                        months={2}
                        direction="horizontal"
                    />
                    <button className="apply-button" onClick={() => setIsOpen(false)}>Apply</button>
                </div>
            )}
        </div>
    );
}

export default DatePicker;
