import React, { useState } from 'react';
import CalendarModal from '../components/CalendarModal';
import './Collection.css';

const Collection = () => {
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [dateRange, setDateRange] = useState({
        startDate: new Date(), // Default to current date
        endDate: new Date(),   // Default to current date
        key: 'selection',
    });

    // Calculate the duration based on the selected dates
    const calculateDuration = () => {
        if (!dateRange.startDate || !dateRange.endDate) return 0; // Fallback in case of missing dates
        const diffTime = Math.abs(dateRange.endDate - dateRange.startDate);
        return Math.ceil(diffTime / (1000 * 60 * 60 * 24)); // Convert milliseconds to days
    };

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1 className="collection-title">The Collection</h1>
            </header>
            <div className="collection-content">
                {/* Button to open the Calendar Modal */}
                <button onClick={() => setCalendarOpen(true)} className="date-button">
                    {dateRange.startDate?.toLocaleDateString() || 'Select Start Date'} - 
                    {dateRange.endDate?.toLocaleDateString() || 'Select End Date'}
                </button>
                <p>Duration: {calculateDuration()} days</p>

                {/* Calendar Modal */}
                {calendarOpen && (
                    <CalendarModal
                        dateRange={dateRange}
                        onDateChange={(newRange) => setDateRange(newRange.selection)}
                        onClose={() => setCalendarOpen(false)}
                    />
                )}
            </div>
        </div>
    );
};

export default Collection;
