import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    const location = useLocation();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            console.log('Login Response:', response.data); // Debug response

            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                console.log('Token Saved:', localStorage.getItem('token')); // Debug token

                const pickupDate = location.state?.pickupDate || 'Default Pickup';
                const returnDate = location.state?.returnDate || 'Default Return';

                console.log('Navigating to Order Form with:', { pickupDate, returnDate }); // Debug navigation
                navigate('/order-form', { state: { pickupDate, returnDate } });
            }
        } catch (error) {
            console.error('Login Error:', error); // Debug errors
            setErrorMessage('Invalid login credentials. Please try again.');
        }
    };

    return (
        <div className="login-container">
            <h2>Sign-in</h2>
            <form onSubmit={handleLogin}>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                <label>Email</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <label>Password</label>
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Log in</button>
            </form>
            <button onClick={() => navigate('/register', { state: { from: location.state?.from } })}>
                Sign up
            </button>
            <a href="/reset-password">Forgot your password?</a>
        </div>
    );
}

export default Login;
