import React, { useState, useEffect } from "react";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const fetchCart = async () => {
      const token = localStorage.getItem("token");
      if (!token) return;

      try {
        const response = await fetch("http://localhost:5000/api/cart", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch cart data");
        }

        const data = await response.json();
        setCartItems(data.cartItems || []);
        setTotalPrice(data.totalPrice || 0);
      } catch (error) {
        console.error(error.message);
      }
    };

    fetchCart();
  }, []);

  const handleRemove = async (gownId) => {
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(`http://localhost:5000/api/cart/${gownId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to remove item from cart");
      }

      setCartItems(cartItems.filter((item) => item.gownId !== gownId));
      setTotalPrice((prev) => prev - cartItems.find((item) => item.gownId === gownId).price);
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <div>
      <h1>Your Cart</h1>
      {cartItems.length > 0 ? (
        <div>
          <ul>
            {cartItems.map((item) => (
              <li key={item.gownId}>
                <p>{item.gownName}</p>
                <p>Price: ₱{item.price}</p>
                <p>
                  Rental Dates: {item.startDate} - {item.endDate}
                </p>
                <button onClick={() => handleRemove(item.gownId)}>Remove</button>
              </li>
            ))}
          </ul>
          <h2>Total: ₱{totalPrice}</h2>
        </div>
      ) : (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
};

export default Cart;
