import React from 'react';
import './MyBookingSidebar.css';

const MyBookingSidebar = ({ cartItems }) => {
    const calculateSubtotal = () =>
        cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="my-booking-sidebar">
            <h3 className="sidebar-title">My Booking</h3>
            {cartItems.length === 0 ? (
                <p className="cart-item-empty">Your booking cart is empty.</p>
            ) : (
                <>
                    {cartItems.map((item, index) => (
                        <div key={index} className="cart-item">
                            <img
                                src={`/assets/${item.image}`}
                                alt={item.name}
                                className="cart-item-image"
                            />
                            <div className="cart-item-details">
                                <p className="item-title">{item.name}</p>
                                <p>Available</p>
                                <div className="quantity-controls">
                                    <button className="quantity-btn">-</button>
                                    <span>{item.quantity}</span>
                                    <button className="quantity-btn">+</button>
                                </div>
                                <p>₱{item.price * item.quantity}</p>
                            </div>
                        </div>
                    ))}
                    <div className="sidebar-footer">
                        <p className="subtotal">
                            Subtotal: ₱{calculateSubtotal()}
                        </p>
                        <button className="view-cart-btn">View Cart</button>
                        <button className="checkout-btn">Checkout</button>
                    </div>
                </>
            )}
        </div>
    );
};

export default MyBookingSidebar;
