import React from 'react';

const MyBookingSidebar = ({ cartItems }) => {
    return (
        <div
            style={{
                width: '400px',
                backgroundColor: '#f8f8f8',
                position: 'fixed',
                top: 0,
                right: 0,
                height: '100%',
                padding: '20px',
                boxShadow: '-2px 0 8px rgba(0, 0, 0, 0.1)',
                overflowY: 'auto',
                zIndex: 1000,
                fontFamily: 'Montserrat, sans-serif',
            }}
        >
            <h3 style={{ fontSize: '1.8rem', fontWeight: 'bold', color: '#213e30', textAlign: 'center' }}>
                My Booking
            </h3>
            {cartItems.length === 0 ? (
                <p style={{ textAlign: 'center', fontSize: '1rem', color: '#213e30' }}>
                    Your booking cart is empty.
                </p>
            ) : (
                cartItems.map((item, index) => (
                    <div
                        key={index}
                        style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            padding: '15px 0',
                            borderBottom: '1px solid #ddd',
                        }}
                    >
                        <div style={{ flex: 1 }}>
                            <p style={{ fontSize: '1rem', fontWeight: 'bold', color: '#213e30' }}>{item.name}</p>
                            <p>{item.dateRange.startDate.toLocaleDateString()} - {item.dateRange.endDate.toLocaleDateString()}</p>
                        </div>
                    </div>
                ))
            )}
            <button
                style={{
                    display: 'block',
                    width: '100%',
                    padding: '12px',
                    backgroundColor: '#d4af37',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    fontWeight: 'bold',
                    marginTop: '10px',
                    cursor: 'pointer',
                    textTransform: 'uppercase',
                }}
            >
                Checkout
            </button>
        </div>
    );
};

export default MyBookingSidebar;
