import React, { useState, useContext, useEffect } from 'react';
import CalendarModal from '../components/CalendarModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import GownDetailsModal from '../components/GownDetailsModal';
import { CartContext } from '../context/CartContext';
import { fetchGowns } from '../services/api';
import './Collection.css';

const Collection = () => {
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [dateRange, setDateRange] = useState({
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    });
    const [gowns, setGowns] = useState([]);
    const [selectedGown, setSelectedGown] = useState(null);
    const { cartItems, addToCart } = useContext(CartContext);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetchGowns();
                setGowns(response.data);
            } catch (error) {
                console.error('Error fetching gowns:', error);
            }
        };
        fetchData();
    }, []);

    const handleDateChange = (ranges) => {
        setDateRange(ranges.selection);
        setCalendarOpen(false);
    };

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1>Dresses for Every Event</h1>
            </header>
            <div className="filters">
                <button onClick={() => setCalendarOpen(true)}>
                    {dateRange.startDate.toLocaleDateString()} - {dateRange.endDate.toLocaleDateString()}
                </button>
                {calendarOpen && (
                    <CalendarModal
                        dateRange={{ selection: dateRange }}
                        onDateChange={handleDateChange}
                        onClose={() => setCalendarOpen(false)}
                    />
                )}
            </div>
            <div className="dress-grid">
                {gowns.map((gown) => (
                    <div key={gown._id} className="dress-card" onClick={() => setSelectedGown(gown)}>
                        <img src={`/assets/${gown.image}`} alt={gown.name} />
                        <h3>{gown.name}</h3>
                        <p>Price: ${gown.price}</p>
                    </div>
                ))}
            </div>
            {selectedGown && (
                <GownDetailsModal
                    gown={selectedGown}
                    onClose={() => setSelectedGown(null)}
                    onAddToCart={(gown) => addToCart({ ...gown, dateRange })}
                />
            )}
            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
};

export default Collection;
