useEffect(() => {
  const token = localStorage.getItem('token');
  if (token) {
    axios
      .get('/auth/me', { headers: { Authorization: `Bearer ${token}` } })
      .then((response) => setUser(response.data))
      .catch(() => localStorage.removeItem('token'));
  }
}, []);
