import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './OrderForm.css';

const OrderForm = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Provide default values to avoid destructuring null or undefined
    const { gowns = [], pickupDate = new Date(), returnDate = new Date() } = location.state || {};

    const [address, setAddress] = useState({
        line1: '',
        line2: '',
        city: '',
        country: '',
        postalCode: '',
        phoneNumber: '',
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Order submitted:', {
            address,
            gowns,
            pickupDate,
            returnDate,
        });
        navigate('/payment'); // Redirect to payment page
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress((prev) => ({ ...prev, [name]: value }));
    };

    return (
        <div className="order-form-container">
            <h2>Order Form</h2>
            <form onSubmit={handleSubmit}>
                <div className="address-section">
                    <h3>Address Details</h3>
                    <input
                        type="text"
                        name="line1"
                        placeholder="Address Line 1"
                        value={address.line1}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="line2"
                        placeholder="Address Line 2"
                        value={address.line2}
                        onChange={handleChange}
                    />
                    <input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={address.city}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={address.country}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="postalCode"
                        placeholder="Postal Code"
                        value={address.postalCode}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="tel"
                        name="phoneNumber"
                        placeholder="Phone Number"
                        value={address.phoneNumber}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="order-summary">
                    <h3>Order Summary</h3>
                    <p>
                        <strong>Pickup Date:</strong> {new Date(pickupDate).toLocaleDateString()}
                    </p>
                    <p>
                        <strong>Return Date:</strong> {new Date(returnDate).toLocaleDateString()}
                    </p>
                    <ul>
                        {gowns.map((gown, index) => (
                            <li key={index}>
                                {gown.name} - {gown.quantity} pcs
                            </li>
                        ))}
                    </ul>
                </div>
                <button type="submit" className="proceed-button">
                    Proceed to Payment
                </button>
            </form>
        </div>
    );
};

export default OrderForm;
