import React, { useState, useEffect } from 'react';
import CalendarModal from '../components/CalendarModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import GownDetailsModal from '../components/GownDetailsModal';
import { fetchGowns } from '../services/api';
import './Collection.css';

const Collection = () => {
    const [gowns, setGowns] = useState([]);
    const [selectedGown, setSelectedGown] = useState(null);
    const [showDetailsModal, setShowDetailsModal] = useState(false);

    useEffect(() => {
        const getGowns = async () => {
            const response = await fetchGowns();
            setGowns(response.data);
        };
        getGowns();
    }, []);

    return (
        <div className="collection-page">
            <h1 className="collection-title">Dresses for Every Event</h1>
            <div className="gown-grid">
                {gowns.map((gown) => (
                    <div
                        key={gown.id}
                        className="gown-card"
                        onClick={() => {
                            setSelectedGown(gown);
                            setShowDetailsModal(true);
                        }}
                    >
                        <img src={gown.image} alt={gown.name} className="gown-image" />
                        <h3>{gown.name}</h3>
                    </div>
                ))}
            </div>
            {showDetailsModal && (
                <GownDetailsModal
                    gown={selectedGown}
                    onClose={() => setShowDetailsModal(false)}
                />
            )}
            <MyBookingSidebar />
        </div>
    );
};

export default Collection;
