import React, { useState, useEffect } from 'react';
import CalendarModal from '../components/CalendarModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import { fetchGowns } from '../services/api'; // Fetch API function
import './Collection.css';

const Collection = () => {
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [dateRange, setDateRange] = useState({
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    });
    const [gowns, setGowns] = useState([]);
    const [cartItems, setCartItems] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    // Fetch gowns on component mount
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetchGowns();
                setGowns(response.data);
            } catch (error) {
                console.error('Error fetching gowns:', error);
            }
        };
        fetchData();
    }, []);

    // Handle date changes from CalendarModal
    const handleDateChange = (ranges) => {
        const { selection } = ranges;
        if (selection.startDate > selection.endDate) {
            alert('Start date cannot be after end date.');
        } else {
            setDateRange(selection);
            setCalendarOpen(false);
        }
    };

    // Add gown to cart
    const addToCart = (gown) => {
        const isAlreadyInCart = cartItems.some((item) => item._id === gown._id);
        if (isAlreadyInCart) {
            alert('This gown is already in your cart.');
        } else {
            setCartItems([...cartItems, { ...gown, dateRange }]);
        }
    };

    // Filter gowns based on search query and availability
    const filteredGowns = gowns.filter((gown) => {
        const matchesSearch =
            gown.name.toLowerCase().includes(searchQuery.toLowerCase());
        const isAvailable = gown.availability.some((range) => {
            const gownStart = new Date(range.startDate);
            const gownEnd = new Date(range.endDate);
            return (
                dateRange.startDate >= gownStart && dateRange.endDate <= gownEnd
            );
        });
        return matchesSearch && isAvailable;
    });

    return (
        <div className="collection-page">
            {/* Header */}
            <header className="collection-banner">
                <h1 className="collection-title">Dresses for Every Event</h1>
            </header>

            {/* Filters */}
            <div className="filter-section">
                <button
                    onClick={() => setCalendarOpen(true)}
                    className="date-button"
                >
                    {dateRange.startDate.toLocaleDateString()} -{' '}
                    {dateRange.endDate.toLocaleDateString()}
                </button>
                <input
                    type="text"
                    placeholder="Search gowns..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                />
            </div>

            {/* Gowns */}
            <div className="dress-grid">
                {filteredGowns.length > 0 ? (
                    filteredGowns.map((gown) => (
                        <div key={gown._id} className="dress-card">
                            <img
                                src={`/assets/${gown.image}`}
                                alt={gown.name}
                                className="dress-image"
                            />
                            <div className="dress-info">
                                <h3>{gown.name}</h3>
                                <p>Price: ${gown.price}</p>
                                <button
                                    onClick={() => addToCart(gown)}
                                    className="add-to-cart-button"
                                >
                                    Add to Cart
                                </button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No gowns found for your search.</p>
                )}
            </div>

            {/* Calendar Modal */}
            {calendarOpen && (
                <CalendarModal
                    dateRange={{ selection: dateRange }}
                    onDateChange={handleDateChange}
                    onClose={() => setCalendarOpen(false)}
                />
            )}

            {/* Sidebar */}
            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
};

export default Collection;
