import React, { useState, useEffect } from "react";
import axios from "../utils/api";

const AdminDashboard = () => {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchBookings = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get("/admin/bookings", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setBookings(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching bookings:", error);
                setLoading(false);
            }
        };

        fetchBookings();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this booking?")) {
            try {
                const token = localStorage.getItem("token");
                await axios.delete(`/admin/booking/${id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setBookings(bookings.filter((booking) => booking._id !== id));
            } catch (error) {
                console.error("Error deleting booking:", error);
            }
        }
    };

    const handleEdit = async (id) => {
        // Implement edit logic (show modal or navigate to an edit page)
        alert("Editing functionality can be added here.");
    };

    if (loading) return <p>Loading bookings...</p>;

    return (
        <div className="p-6">
            <h1 className="text-2xl font-bold mb-4">Admin Dashboard</h1>
            <table className="table-auto w-full border-collapse border border-gray-400">
                <thead>
                    <tr className="bg-green-700 text-white">
                        <th className="p-2 border">Client Email</th>
                        <th className="p-2 border">Gown IDs</th>
                        <th className="p-2 border">Total Price</th>
                        <th className="p-2 border">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {bookings.map((booking) => (
                        <tr key={booking._id} className="hover:bg-gray-100">
                            <td className="p-2 border">{booking.userId?.email}</td>
                            <td className="p-2 border">{booking.items.map((item) => item.gownId).join(", ")}</td>
                            <td className="p-2 border">₱{booking.totalPrice}</td>
                            <td className="p-2 border">
                                <button
                                    onClick={() => handleEdit(booking._id)}
                                    className="mr-2 bg-blue-500 text-white px-4 py-2 rounded"
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDelete(booking._id)}
                                    className="bg-red-500 text-white px-4 py-2 rounded"
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
