import React, { useState, useEffect } from 'react';
import axios from '../services/api'; // For API requests
import { useNavigate } from 'react-router-dom';
import './OrderForm.css';

function OrderForm() {
    const [address, setAddress] = useState({
        line1: '',
        line2: '',
        city: '',
        country: '',
        postalCode: '',
        phoneNumber: '',
    });
    const [error, setError] = useState('');
    const [cartItems, setCartItems] = useState([]); // Fetch from context or API
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch cart items or set from context/localStorage if applicable
        const fetchCartItems = async () => {
            try {
                const response = await axios.get('/api/cart');
                setCartItems(response.data);
            } catch (err) {
                console.error('Error fetching cart items:', err);
            }
        };

        fetchCartItems();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/bookings', { address, items: cartItems });
            navigate('/payment'); // Redirect to payment page
        } catch (error) {
            setError('Error processing order. Please try again.');
            console.error(error);
        }
    };

    return (
        <div className="order-form-container">
            <h2>Order Form</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="address-section">
                    <h3>Address Details</h3>
                    <input
                        type="text"
                        name="line1"
                        placeholder="Address Line 1"
                        value={address.line1}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="line2"
                        placeholder="Address Line 2"
                        value={address.line2}
                        onChange={handleChange}
                    />
                    <input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={address.city}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={address.country}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="postalCode"
                        placeholder="Postal Code"
                        value={address.postalCode}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="tel"
                        name="phoneNumber"
                        placeholder="Phone Number"
                        value={address.phoneNumber}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="order-summary">
                    <h3>Order Summary</h3>
                    <ul>
                        {cartItems.map((item) => (
                            <li key={item.id}>
                                {item.name} - {item.quantity} pcs
                            </li>
                        ))}
                    </ul>
                </div>
                <button type="submit" className="proceed-button">Proceed to Payment</button>
            </form>
        </div>
    );
}

export default OrderForm;
