import React, { useState } from 'react';
import Modal from 'react-modal';
import './Collection.css';

const dresses = [
    { id: 1, name: 'Pink Gownn', price: 'P.1000', days: '4 days', image: 'pinkgown.jpg', description: 'Antique rose full-length elegant gown...' },
    { id: 2, name: 'Blue Gown', price: 'P.1000', days: '4 days', image: 'bluegown.jpg', description: 'Sparkling dress for special occasions...' },
    // Add more dress items here
];

Modal.setAppElement('#root');

function Collection() {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedDress, setSelectedDress] = useState(null);
    const [pickupDate, setPickupDate] = useState('');
    const [returnDate, setReturnDate] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    const openModal = (dress) => {
        setSelectedDress(dress);
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
        setSelectedDress(null);
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handlePickupDateChange = (e) => {
        setPickupDate(e.target.value);
    };

    const handleReturnDateChange = (e) => {
        setReturnDate(e.target.value);
    };

    const filteredDresses = dresses.filter((dress) => {
        const matchesSearch = dress.name.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesSearch;
    });

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1 className="collection-title">The Collection</h1>
            </header>
            <div className="collection-content">
                <h2 className="collection-subtitle">Dresses for every event</h2>
                <div className="filter-search">
                    <input type="date" value={pickupDate} onChange={handlePickupDateChange} placeholder="Select pickup date" />
                    <input type="date" value={returnDate} onChange={handleReturnDateChange} placeholder="Select return date" />
                    <input type="text" value={searchQuery} onChange={handleSearchChange} placeholder="Search products" />
                </div>
                <div className="dress-grid">
                    {filteredDresses.map((dress) => (
                        <div key={dress.id} className="dress-card" onClick={() => openModal(dress)}>
                            <img src={require(`../assets/${dress.image}`)} alt={dress.name} className="dress-image" />
                            <div className="dress-info">
                                <h3>{dress.name}</h3>
                                <p>{dress.days} {dress.price}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {selectedDress && (
                <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="dress-modal" overlayClassName="modal-overlay">
                    <button className="close-modal" onClick={closeModal}>X</button>
                    <img src={require(`../assets/${selectedDress.image}`)} alt={selectedDress.name} className="modal-image" />
                    <div className="modal-info">
                        <h2>{selectedDress.name}</h2>
                        <p>{selectedDress.price}</p>
                        <p>{selectedDress.description}</p>
                        <button className="add-to-cart">Add to cart</button>
                    </div>
                </Modal>
            )}
        </div>
    );
}

export default Collection;
