import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MyBookingSidebar.css';

const MyBookingSidebar = ({ cartItems }) => {
    const navigate = useNavigate();

    const handleCheckout = () => {
        navigate('/order-form', { state: { cartItems } }); // Pass cart items to Order Form
    };

    return (
        <div className="my-booking-sidebar">
            <h3>My Booking</h3>
            {cartItems.length > 0 ? (
                cartItems.map((item, index) => (
                    <div key={index} className="cart-item">
                        <p><strong>{item.name}</strong></p>
                        <p>
                            {item.dateRange.startDate.toLocaleDateString()} -{' '}
                            {item.dateRange.endDate.toLocaleDateString()}
                        </p>
                        <p>Duration: {item.duration} days</p>
                        <p>Price: ${item.price * item.duration}</p> {/* Price multiplied by duration */}
                    </div>
                ))
            ) : (
                <p>Your cart is empty.</p>
            )}
            <button onClick={handleCheckout}>Checkout</button>
        </div>
    );
};

export default MyBookingSidebar;
