import React, { useEffect, useState } from "react";
import { fetchBookings, updateBookingStatus, deleteBooking } from "../services/api";

const AdminBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [filter, setFilter] = useState(""); // Filter: pending, completed, or empty for all

  useEffect(() => {
    const loadBookings = async () => {
      try {
        const data = await fetchBookings(filter);
        setBookings(data);
      } catch (error) {
        console.error("Error fetching bookings:", error);
      }
    };

    loadBookings();
  }, [filter]); // Refetch when filter changes

  const handleStatusChange = async (id, status) => {
    try {
      const updatedBooking = await updateBookingStatus(id, status);
      setBookings(
        bookings.map((booking) =>
          booking._id === updatedBooking._id ? updatedBooking : booking
        )
      );
    } catch (error) {
      console.error("Error updating booking status:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteBooking(id);
      setBookings(bookings.filter((booking) => booking._id !== id));
    } catch (error) {
      console.error("Error deleting booking:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Bookings Management</h1>

      {/* Filter Options */}
      <div className="mb-4">
        <button onClick={() => setFilter("")} className="mr-2 px-4 py-2 bg-blue-500 text-white">
          All
        </button>
        <button onClick={() => setFilter("pending")} className="mr-2 px-4 py-2 bg-yellow-500 text-white">
          Pending
        </button>
        <button onClick={() => setFilter("completed")} className="px-4 py-2 bg-green-500 text-white">
          Completed
        </button>
      </div>

      {/* Bookings Table */}
      <table className="min-w-full bg-white border">
        <thead>
          <tr>
            <th className="px-4 py-2 border">Booking ID</th>
            <th className="px-4 py-2 border">Client Email</th>
            <th className="px-4 py-2 border">Gown Name</th>
            <th className="px-4 py-2 border">Pickup Date</th>
            <th className="px-4 py-2 border">Return Date</th>
            <th className="px-4 py-2 border">Total Price</th>
            <th className="px-4 py-2 border">Status</th>
            <th className="px-4 py-2 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking._id}>
              <td className="px-4 py-2 border">{booking._id}</td>
              <td className="px-4 py-2 border">{booking.clientEmail}</td>
              <td className="px-4 py-2 border">{booking.gownName}</td>
              <td className="px-4 py-2 border">{new Date(booking.pickupDate).toLocaleDateString()}</td>
              <td className="px-4 py-2 border">{new Date(booking.returnDate).toLocaleDateString()}</td>
              <td className="px-4 py-2 border">₱{booking.totalPrice}</td>
              <td className="px-4 py-2 border">{booking.status}</td>
              <td className="px-4 py-2 border">
                <button
                  onClick={() => handleStatusChange(booking._id, "completed")}
                  className="bg-green-500 text-white px-2 py-1 rounded mr-2"
                >
                  Mark Completed
                </button>
                <button
                  onClick={() => handleDelete(booking._id)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminBookings;
