import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from '../services/api';

function OrderForm() {
    const { state } = useLocation();
    const { cartItems, dateRange } = state || {};
    const [address, setAddress] = useState({ line1: '', city: '', country: '' });
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('/api/bookings', { cartItems, dateRange, address });
            navigate('/payment'); // Redirect to payment page
        } catch (error) {
            console.error('Error submitting order:', error);
        }
    };

    return (
        <div>
            <h1>Order Form</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Address Line 1" onChange={(e) => setAddress({ ...address, line1: e.target.value })} />
                <input type="text" placeholder="City" onChange={(e) => setAddress({ ...address, city: e.target.value })} />
                <input type="text" placeholder="Country" onChange={(e) => setAddress({ ...address, country: e.target.value })} />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default OrderForm;
