import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Cart.css";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  // Fetch cart items
  useEffect(() => {
    const fetchCart = async () => {
        const token = localStorage.getItem("token");
        console.log("Fetched token:", token); // Debug token

        if (!token) {
            console.error("No token found");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/cart", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error("Error fetching cart:", errorData.message);
                return;
            }

            const data = await response.json();
            console.log("Fetched Cart Items:", data.cartItems);
            console.log("Total Price:", data.totalPrice);

            setCartItems(data.cartItems || []);
            setTotalPrice(data.totalPrice || 0);
        } catch (error) {
            console.error("Error fetching cart:", error.message);
        }
    };

    fetchCart();
}, []);

  const handleRemove = async (itemId) => {
    const token = localStorage.getItem("token");
    try {
      const response = await fetch(`http://localhost:5000/api/cart/${itemId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to remove item");
      }

      // Refresh the cart
      setCartItems((prevItems) => prevItems.filter((item) => item._id !== itemId));
      setTotalPrice((prevPrice) => prevPrice - cartItems.find((item) => item._id === itemId).price);
    } catch (error) {
      console.error("Error removing item:", error.message);
    }
  };

  const handleCheckout = () => {
    alert("Proceeding to checkout...");
    navigate("/checkout");
  };

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      {cartItems.length > 0 ? (
        <div>
          <ul className="cart-list">
            {cartItems.map((item) => (
              <li key={item._id} className="cart-item">
                <div className="cart-details">
                  <h2>{item.gownId}</h2>
                  <p>Price: ₱{item.price}</p>
                  <p>
                    Rental Dates: {new Date(item.startDate).toLocaleDateString()} -{" "}
                    {new Date(item.endDate).toLocaleDateString()}
                  </p>
                  <button onClick={() => handleRemove(item._id)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
          <h2>Total Price: ₱{totalPrice}</h2>
          <button className="checkout-button" onClick={handleCheckout}>
            Proceed to Checkout
          </button>
        </div>
      ) : (
        <p>Your cart is empty!</p>
      )}
    </div>
  );
};

export default Cart;
