import React, { useState, useEffect } from 'react';
import CalendarModal from '../components/CalendarModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import { fetchGowns } from '../services/api'; // Import the correct function
import './Collection.css';

const Collection = () => {
    const [calendarOpen, setCalendarOpen] = useState(false); // For toggling calendar modal
    const [dateRange, setDateRange] = useState({
        startDate: new Date(), // Today's date as the default start
        endDate: new Date(), // Today's date as the default end
        key: 'selection', // Required key for react-date-range
    });
    const [gowns, setGowns] = useState([]); // Store fetched gowns
    const [cartItems, setCartItems] = useState([]); // Store items added to cart
    const [searchQuery, setSearchQuery] = useState(''); // For search functionality

    // Fetch gowns from the API when the component mounts
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetchGowns(); // Call API to fetch gowns
                setGowns(response.data); // Update gowns state
            } catch (error) {
                console.error('Failed to fetch gowns:', error); // Log error
            }
        };
        fetchData();
    }, []);

    // Handle date changes from the CalendarModal
    const handleDateChange = (ranges) => {
        const { selection } = ranges; // Destructure selection from the passed ranges
        if (selection) {
            setDateRange(selection); // Update the dateRange state
        } else {
            console.error('Invalid date range selected.'); // Log if selection is undefined
        }
        setCalendarOpen(false); // Close the calendar modal
    };

    // Add gown to the cart with date range
    const addToCart = (gown) => {
        const isAlreadyInCart = cartItems.some((item) => item._id === gown._id); // Check if already in cart
        if (isAlreadyInCart) {
            alert('This gown is already in your cart.');
        } else {
            setCartItems([...cartItems, { ...gown, dateRange }]); // Add gown to the cart
        }
    };

    // Filter gowns based on the search query
    const filteredGowns = gowns.filter((gown) =>
        gown.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="collection-page">
            {/* Header Section */}
            <header className="collection-banner">
                <h1 className="collection-title">Dresses for Every Event</h1>
            </header>

            {/* Main Content */}
            <div className="collection-content">
                {/* Filters: Date Picker and Search */}
                <div className="filter-section">
                    <button
                        onClick={() => setCalendarOpen(true)}
                        className="date-button"
                    >
                        {dateRange.startDate.toLocaleDateString()} - {dateRange.endDate.toLocaleDateString()}
                    </button>
                    <input
                        type="text"
                        placeholder="Search gowns..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="search-input"
                    />
                </div>

                {/* Gowns Display */}
                <div className="dress-grid">
                    {filteredGowns.length > 0 ? (
                        filteredGowns.map((gown) => (
                            <div key={gown._id} className="dress-card">
                                <img
                                    src={`/assets/${gown.image}`} // Dynamically load images
                                    alt={gown.name}
                                    className="dress-image"
                                />
                                <div className="dress-info">
                                    <h3>{gown.name}</h3>
                                    <p>Price: ${gown.price}</p>
                                    <button
                                        onClick={() => addToCart(gown)}
                                        className="add-to-cart-button"
                                    >
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No gowns found for your search.</p>
                    )}
                </div>
            </div>

            {/* Calendar Modal */}
            {calendarOpen && (
                <CalendarModal
                    dateRange={{ selection: dateRange }} // Pass the current date range
                    onDateChange={handleDateChange}
                    onClose={() => setCalendarOpen(false)}
                />
            )}

            {/* Sidebar */}
            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
};

export default Collection;
