import React, { useState } from 'react';
import Modal from 'react-modal';
import { DateRange } from 'react-date-range';
import { enGB } from 'date-fns/locale';
import './Collection.css';

const dresses = [
    { id: 1, name: 'Pink Gown', price: 'P.1000', days: '4 days', image: 'pinkgown.jpg', description: 'Antique rose full-length elegant gown...' },
    { id: 2, name: 'Blue Gown', price: 'P.1000', days: '4 days', image: 'bluegown.jpg', description: 'Sparkling dress for special occasions...' },
    // Add more dress items here
];

function Collection() {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedDress, setSelectedDress] = useState(null);
    const [pickupDate, setPickupDate] = useState(null);
    const [returnDate, setReturnDate] = useState(null);
    const [rentalDuration, setRentalDuration] = useState(0);
    const [showCalendar, setShowCalendar] = useState(false);

    const openModal = (dress) => {
        setSelectedDress(dress);
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
        setSelectedDress(null);
    };

    const handleSelectDateRange = (ranges) => {
        const start = ranges.selection.startDate;
        const end = ranges.selection.endDate;
        setPickupDate(start);
        setReturnDate(end);

        const duration = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
        setRentalDuration(duration);
        setShowCalendar(false);
    };

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1 className="collection-title">The Collection</h1>
            </header>
            <div className="collection-content">
                <h2 className="collection-subtitle">Dresses for every event</h2>
                <div className="filter-search">
                    <input
                        type="text"
                        value={pickupDate ? `Pickup Date: ${pickupDate.toLocaleDateString()}` : 'Select Pickup Date'}
                        readOnly
                        onClick={() => setShowCalendar(true)}
                        className="date-input"
                    />
                    <input
                        type="text"
                        value={returnDate ? `Return Date: ${returnDate.toLocaleDateString()}` : 'Select Return Date'}
                        readOnly
                        onClick={() => setShowCalendar(true)}
                        className="date-input"
                    />
                    <input
                        type="text"
                        value={rentalDuration ? `Duration: ${rentalDuration} days` : 'Rental Duration'}
                        readOnly
                        className="duration-input"
                    />
                </div>

                {showCalendar && (
                    <DateRange
                        onChange={handleSelectDateRange}
                        editableDateInputs={true}
                        moveRangeOnFirstSelection={false}
                        ranges={[{
                            startDate: pickupDate || new Date(),
                            endDate: returnDate || new Date(),
                            key: 'selection',
                        }]}
                        months={2}
                        direction="horizontal"
                        locale={enGB}
                        className="date-range-calendar"
                    />
                )}

                <div className="dress-grid">
                    {dresses.map((dress) => (
                        <div key={dress.id} className="dress-card" onClick={() => openModal(dress)}>
                            <img src={require(`../assets/${dress.image}`)} alt={dress.name} className="dress-image" />
                            <div className="dress-info">
                                <h3>{dress.name}</h3>
                                <p>{dress.days} {dress.price}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {selectedDress && (
                <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="dress-modal" overlayClassName="modal-overlay">
                    <button className="close-modal" onClick={closeModal}>X</button>
                    <img src={require(`../assets/${selectedDress.image}`)} alt={selectedDress.name} className="modal-image" />
                    <div className="modal-info">
                        <h2>{selectedDress.name}</h2>
                        <p>{selectedDress.price}</p>
                        <p>{selectedDress.description}</p>
                        <button className="add-to-cart">Add to cart</button>
                    </div>
                </Modal>
            )}
        </div>
    );
}

export default Collection;
