import React from 'react';
import { DateRange } from 'react-date-range';
import 'react-date-range/dist/styles.css'; // Main styles
import 'react-date-range/dist/theme/default.css'; // Default theme styles

const CalendarModal = ({ dateRange, onDateChange, onClose }) => {
    // Ensure dateRange is valid
    const validDateRange = dateRange?.startDate && dateRange?.endDate ? dateRange : {
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    };

    return (
        <div className="calendar-modal">
            <button onClick={onClose} className="close-button">
                Close
            </button>
            <DateRange
                editableDateInputs={true}
                onChange={(ranges) => onDateChange(ranges)} // Pass the entire ranges object
                moveRangeOnFirstSelection={false}
                ranges={[validDateRange]} // Pass the current date range
            />
        </div>
    );
};

export default CalendarModal;
