import React from 'react';
import './MyBookingSidebar.css';

const MyBookingSidebar = ({ cartItems }) => {
    const calculateSubtotal = () =>
        cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

    return (
        <div className="my-booking-sidebar">
            <h3>My Booking</h3>
            {cartItems.length === 0 ? (
                <p className="cart-item-empty">Your booking cart is empty.</p>
            ) : (
                <>
                    <div className="cart-items">
                        {cartItems.map((item, index) => (
                            <div key={index} className="cart-item">
                                <div className="cart-item-details">
                                    <img
                                        src={`/assets/${item.image}`}
                                        alt={item.name}
                                        className="cart-item-image"
                                    />
                                    <div>
                                        <p className="item-title">{item.name}</p>
                                        <p>
                                            {item.dateRange.startDate.toLocaleDateString()} -{' '}
                                            {item.dateRange.endDate.toLocaleDateString()}
                                        </p>
                                        <p>Duration: {item.duration} days</p>
                                    </div>
                                </div>
                                <div className="cart-item-actions">
                                    <p>€{item.price.toFixed(2)}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="cart-summary">
                        <p className="subtotal">
                            Subtotal: €{calculateSubtotal().toFixed(2)}
                        </p>
                        <button className="view-cart-button">View Cart</button>
                        <button className="checkout-button">Checkout</button>
                    </div>
                </>
            )}
        </div>
    );
};

export default MyBookingSidebar;
