import React, { useState, useEffect } from 'react';
import CalendarModal from '../components/CalendarModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import { fetchGowns } from '../services/api'; // Import the correct function
import './Collection.css';

const Collection = () => {
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [dateRange, setDateRange] = useState({
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    });
    const [gowns, setGowns] = useState([]);
    const [cartItems, setCartItems] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    // Fetch Gowns
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetchGowns();
                setGowns(response.data);
            } catch (error) {
                console.error('Failed to fetch gowns:', error);
            }
        };
        fetchData();
    }, []);

    // Handle Date Change
    const handleDateChange = (newRange) => {
        setDateRange(newRange);
        setCalendarOpen(false);
    };

    // Add Gown to Cart
    const addToCart = (gown) => {
        setCartItems([...cartItems, { ...gown, dateRange }]);
    };

    // Filter Gowns
    const filteredGowns = gowns.filter((gown) =>
        gown.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1 className="collection-title">Dresses for Every Event</h1>
            </header>
            <div className="collection-content">
                <button onClick={() => setCalendarOpen(true)} className="date-button">
                    {dateRange.startDate.toLocaleDateString()} - {dateRange.endDate.toLocaleDateString()}
                </button>
                <input
                    type="text"
                    placeholder="Search gowns..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                />
                <div className="dress-grid">
                    {filteredGowns.map((gown) => (
                        <div key={gown._id} className="dress-card">
                            <img
                                src={`/assets/${gown.image}`}
                                alt={gown.name}
                                className="dress-image"
                            />
                            <div className="dress-info">
                                <h3>{gown.name}</h3>
                                <p>Price: ${gown.price}</p>
                                <button onClick={() => addToCart(gown)}>Add to Cart</button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            {calendarOpen && (
                <CalendarModal
                    dateRange={dateRange}
                    onDateChange={handleDateChange}
                    onClose={() => setCalendarOpen(false)}
                />
            )}
            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
};

export default Collection;
