import React, { useState } from 'react';
import CalendarModal from '../components/CalendarModal';
import GownDetailsModal from '../components/GownDetailsModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import './Collection.css';

const dresses = [
    { id: 1, name: 'Pink Gown', price: 100, image: 'pinkgown.jpg', description: 'Elegant full-length gown...' },
    { id: 2, name: 'Blue Gown', price: 120, image: 'bluegown.jpg', description: 'Classic and stylish gown...' },
    // Add more dresses as needed
];

function Collection() {
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [selectedDress, setSelectedDress] = useState(null);
    const [dateRange, setDateRange] = useState({
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    });
    const [cartItems, setCartItems] = useState([]);

    const handleDateChange = (range) => {
        setDateRange(range.selection);
        setCalendarOpen(false);
    };

    const handleAddToCart = (gown) => {
        setCartItems([...cartItems, { ...gown, dateRange }]);
        setSelectedDress(null);
    };

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1>The Collection</h1>
            </header>
            <div className="collection-content">
                <div className="dress-grid">
                    {dresses.map((dress) => (
                        <div key={dress.id} className="dress-card" onClick={() => setSelectedDress(dress)}>
                            <img src={require(`../assets/${dress.image}`)} alt={dress.name} className="dress-image" />
                            <div className="dress-info">
                                <h3>{dress.name}</h3>
                                <p>Price: ${dress.price}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {calendarOpen && (
                <CalendarModal dateRange={dateRange} onDateChange={handleDateChange} onClose={() => setCalendarOpen(false)} />
            )}

            {selectedDress && (
                <GownDetailsModal
                    gown={selectedDress}
                    onClose={() => setSelectedDress(null)}
                    onAddToCart={handleAddToCart}
                    availableFrom={dateRange.startDate}
                    availableUntil={dateRange.endDate}
                />
            )}

            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
}

export default Collection;
