import axios from 'axios';

const API = axios.create({
    baseURL: 'http://localhost:5000', // Adjust the base URL if necessary
});

// Authentication APIs
export const registerUser = async (userData) => {
    return await API.post('/api/auth/register', userData);
};

export const loginUser = async (userData) => {
    return await API.post('/api/auth/login', userData);
};

// Gown APIs
export const fetchGowns = async () => {
    return await API.get('/api/gowns');
};

// Booking APIs
export const createBooking = async (bookingData) => {
    return await API.post('/api/bookings', bookingData);
};

export const getUserBookings = async (userId) => {
    return await API.get(`/api/bookings/${userId}`);
};

// Add any additional API calls as needed
