import React, { useState, useEffect } from "react";
import axios from "../utils/api"; // Utility to handle API calls
import "./Collection.css";

const Collection = () => {
  const [gowns, setGowns] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    // Fetch gowns from the backend
    const fetchGowns = async () => {
      try {
        const response = await axios.get("/gowns"); // Assuming /gowns endpoint
        setGowns(response.data);
      } catch (error) {
        console.error("Error fetching gowns:", error);
      }
    };

    fetchGowns();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  const filteredGowns = gowns.filter((gown) =>
    gown.name.toLowerCase().includes(searchTerm)
  );

  return (
    <div className="collection-container">
      <h1 className="collection-title">Our Collection</h1>
      <input
        type="text"
        className="search-bar"
        placeholder="Search gowns..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="gown-grid">
        {filteredGowns.length > 0 ? (
          filteredGowns.map((gown) => (
            <div key={gown._id} className="gown-card">
              <img
                src={gown.image}
                alt={gown.name}
                className="gown-image"
              />
              <h2 className="gown-name">{gown.name}</h2>
              <p className="gown-price">From ${gown.price} per day</p>
              <button
                className="view-details-button"
                onClick={() => window.location.href = `/gown/${gown._id}`}
              >
                View Details
              </button>
            </div>
          ))
        ) : (
          <p className="no-gowns-message">No gowns found.</p>
        )}
      </div>
    </div>
  );
};

export default Collection;
