// client/src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';
import axios from '../services/api';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true); // To manage loading state on app load

    // Load user from localStorage if a token is present
    useEffect(() => {
        const loadUser = async () => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    // Optional: Set token as default Axios header
                    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
                    const response = await axios.get('/api/auth/me'); // Endpoint to get user info
                    setUser(response.data.user);
                } catch (error) {
                    console.error('Failed to fetch user:', error);
                    localStorage.removeItem('token'); // Remove invalid token
                }
            }
            setLoading(false);
        };

        loadUser();
    }, []);

    // Login function
    const login = async (email, password) => {
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token); // Save token in localStorage
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            setUser(user);
        } catch (error) {
            console.error('Login error:', error.response?.data || error.message);
        }
    };

    // Register function
    const register = async (name, email, password) => {
        try {
            const response = await axios.post('/api/auth/register', { name, email, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token); // Save token in localStorage
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            setUser(user);
        } catch (error) {
            console.error('Registration error:', error.response?.data || error.message);
        }
    };

    // Logout function
    const logout = () => {
        localStorage.removeItem('token');
        delete axios.defaults.headers.common['Authorization'];
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, loading, login, register, logout }}>
            {!loading && children}
        </AuthContext.Provider>
    );
};
