import React, { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const Cart = () => {
  const { user } = useContext(AuthContext);
  const cartItems = JSON.parse(localStorage.getItem("cartItems")) || []; // Example cart data

  if (!user) {
    return <p className="text-center">Please log in to view your cart.</p>;
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Your Cart</h1>
      {cartItems.length === 0 ? (
        <p className="text-center text-lg">Your cart is empty.</p>
      ) : (
        <div className="grid gap-4">
          {cartItems.map((item, index) => (
            <div
              key={index}
              className="p-4 bg-white shadow-lg rounded-md flex justify-between items-center"
            >
              <div>
                <h3 className="font-semibold text-lg">{item.name}</h3>
                <p>Price: â‚±{item.price}</p>
              </div>
              <div>
                <button className="bg-red-500 text-white px-4 py-2 rounded">
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
