// client/src/pages/OrderForm.js
import React, { useState } from 'react';
import './OrderForm.css';

function OrderForm() {
    const [address, setAddress] = useState({
        country: '',
        line1: '',
        line2: '',
        city: '',
        county: '',
        postalCode: '',
        phoneNumber: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress({ ...address, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit form data to the server
        console.log('Order submitted with address:', address);
    };

    return (
        <div className="order-form-container">
            <h2>Order Form</h2>
            <form onSubmit={handleSubmit}>
                <label>Country*</label>
                <input
                    type="text"
                    name="country"
                    value={address.country}
                    onChange={handleChange}
                    required
                />

                <label>Address line 1*</label>
                <input
                    type="text"
                    name="line1"
                    value={address.line1}
                    onChange={handleChange}
                    required
                />

                <label>Address line 2</label>
                <input
                    type="text"
                    name="line2"
                    value={address.line2}
                    onChange={handleChange}
                />

                <label>City*</label>
                <input
                    type="text"
                    name="city"
                    value={address.city}
                    onChange={handleChange}
                    required
                />

                <label>County*</label>
                <input
                    type="text"
                    name="county"
                    value={address.county}
                    onChange={handleChange}
                    required
                />

                <label>Postal Code*</label>
                <input
                    type="text"
                    name="postalCode"
                    value={address.postalCode}
                    onChange={handleChange}
                    required
                />

                <label>Phone Number*</label>
                <input
                    type="text"
                    name="phoneNumber"
                    value={address.phoneNumber}
                    onChange={handleChange}
                    required
                />

                <div className="terms">
                    <input type="checkbox" required />
                    <label>By ticking this box I confirm that I have read and accepted the terms & conditions</label>
                </div>

                <button type="submit" className="continue-button">Continue to payment</button>
            </form>
        </div>
    );
}

export default OrderForm;
