import React, { useState, useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from '../services/api'; // Use your configured Axios instance
import { AuthContext } from '../context/AuthContext';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const { loginUser } = useContext(AuthContext); // Use loginUser from AuthContext
    const navigate = useNavigate();
    const location = useLocation();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            if (response.data.token) {
                loginUser(response.data.token); // Save token and set user context
                const { pickupDate, returnDate } = location.state || {};
                navigate('/order-form', { state: { pickupDate, returnDate } });
            }
        } catch (error) {
            setErrorMessage('Invalid login credentials. Please try again.');
        }
    };

    return (
        <div className="login-container">
            <h2>Sign-in</h2>
            <form onSubmit={handleLogin}>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                <label>Email</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <label>Password</label>
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Log in</button>
            </form>
        </div>
    );
}

export default Login;
