import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]); // Define cartItems state
  const [totalPrice, setTotalPrice] = useState(0); // Define totalPrice state
  const navigate = useNavigate();

  // Fetch cart items on component mount
  useEffect(() => {
    const fetchCart = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await fetch('http://localhost:5000/api/cart', {
          headers: {
            Authorization: `Bearer ₱{token}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch cart data');
        }

        const data = await response.json();
        setCartItems(data.cartItems || []);
        setTotalPrice(data.totalPrice || 0);
      } catch (error) {
        console.error('Error fetching cart:', error.message);
      }
    };

    fetchCart();
  }, []); // Empty dependency array ensures this runs only once on mount

  // Remove an item from the cart
  const handleRemove = (index) => {
    const updatedCart = [...cartItems];
    updatedCart.splice(index, 1); // Remove the item at the given index
    setCartItems(updatedCart); // Update the cartItems state
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    const total = updatedCart.reduce((sum, item) => sum + item.price, 0);
    setTotalPrice(total); // Update the totalPrice state
  };

  // Proceed to checkout
  const handleCheckout = () => {
    alert('Proceeding to checkout...');
    navigate('/checkout'); // Navigate to the checkout page
  };

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      {cartItems.length > 0 ? (
        <div>
          <ul className="cart-list">
            {cartItems.map((item, index) => (
              <li key={index} className="cart-item">
                <img src={item.image} alt={item.name} className="cart-image" />
                <div className="cart-details">
                  <h2>{item.name}</h2>
                  <p>Category: {item.category}</p>
                  <p>Price: ₱{item.price}</p>
                  <p>
                    Rental Dates: {new Date(item.startDate).toLocaleDateString()} -{' '}
                    {new Date(item.endDate).toLocaleDateString()}
                  </p>
                  <button onClick={() => handleRemove(index)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
          <h2>Total Price: ₱{totalPrice}</h2>
          <button className="checkout-button" onClick={handleCheckout}>
            Proceed to Checkout
          </button>
        </div>
      ) : (
        <p>Your cart is empty!</p>
      )}
    </div>
  );
};

export default Cart;
