import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const Checkout = () => {
  const location = useLocation();
  const { cartItems, totalPrice } = location.state || {};
  const navigate = useNavigate();

  const handlePayment = () => {
    alert("Payment successful!");
    navigate("/"); // Navigate back to the homepage or order confirmation page
  };

  if (!cartItems || cartItems.length === 0) {
    return <p>No items to checkout.</p>;
  }

  return (
    <div>
      <h1>Checkout</h1>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            Gown ID: {item.gownId}, Price: ₱{item.price}, Start Date:{" "}
            {new Date(item.startDate).toISOString()}, End Date:{" "}
            {new Date(item.endDate).toISOString()}
          </li>
        ))}
      </ul>
      <h2>Total Price: ₱{totalPrice}</h2>
      <button onClick={handlePayment}>Pay Now</button>
    </div>
  );
};

export default Checkout;
