import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from '../services/api';
import './Register.css';

function Register() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const location = useLocation();

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (password !== confirmPassword) {
            setError('Passwords do not match');
            return;
        }

        try {
            const response = await axios.post('/api/auth/register', { name, email, password });
            localStorage.setItem('token', response.data.token); // Store the token in localStorage if needed

            // Redirect to the previous page or the order form
            const redirectPath = location.state?.from || '/order-form';
            navigate(redirectPath);
        } catch (error) {
            const serverError = error.response?.data?.message || 'Registration failed. Please try again.';
            setError(serverError);
            console.error('Registration error:', error);
        }
    };

    return (
        <div className="register-container">
            <h2>Sign Up</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Full Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    className="input-field"
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    className="input-field"
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    className="input-field"
                />
                <input
                    type="password"
                    placeholder="Confirm Password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                    className="input-field"
                />
                <button type="submit" className="register-button">Register</button>
            </form>
        </div>
    );
}

export default Register;
