import React from 'react';
import './MyBookingSidebar.css';

const MyBookingSidebar = ({ cartItems }) => {
    return (
        <div className="my-booking-sidebar">
            <h3>My Booking</h3>
            {cartItems.length === 0 ? (
                <p>Your booking cart is empty.</p>
            ) : (
                cartItems.map((item, index) => {
                    const startDate = new Date(item.dateRange.startDate);
                    const endDate = new Date(item.dateRange.endDate);
                    const duration =
                        (endDate - startDate) / (1000 * 60 * 60 * 24) + 1;

                    return (
                        <div key={index} className="cart-item">
                            <p>{item.name}</p>
                            <p>
                                {startDate.toLocaleDateString()} -{' '}
                                {endDate.toLocaleDateString()}
                            </p>
                            <p>Duration: {duration} days</p>
                        </div>
                    );
                })
            )}
            <button onClick={() => alert('Proceed to checkout!')}>Checkout</button>
        </div>
    );
};

// Default export
export default MyBookingSidebar;
