import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './OrderForm.css';

const OrderForm = () => {
    const location = useLocation();
    const navigate = useNavigate();

    const { gowns = [], pickupDate = new Date(), returnDate = new Date() } = location.state || {};

    const [address, setAddress] = useState({
        line1: '',
        line2: '',
        city: '',
        country: '',
        postalCode: '',
        phoneNumber: '',
    });

    const [loading, setLoading] = useState(false); // Loading state
    const [error, setError] = useState(''); // Error state

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validate address fields
        if (!address.line1 || !address.city || !address.country || !address.postalCode || !address.phoneNumber) {
            setError('Please fill in all required fields.');
            return;
        }

        setError(''); // Clear previous errors
        setLoading(true); // Show loading spinner

        // Simulate saving the order and navigating to the payment page
        setTimeout(() => {
            console.log('Order submitted:', {
                address,
                gowns,
                pickupDate,
                returnDate,
            });
            navigate('/payment', { state: { address, gowns, pickupDate, returnDate } });
            setLoading(false); // Hide loading spinner
        }, 2000);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAddress((prev) => ({ ...prev, [name]: value }));
    };

    return (
        <div className="order-form-container">
            <h2>Order Form</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="address-section">
                    <h3>Address Details</h3>
                    <input
                        type="text"
                        name="line1"
                        placeholder="Address Line 1"
                        value={address.line1}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="line2"
                        placeholder="Address Line 2"
                        value={address.line2}
                        onChange={handleChange}
                    />
                    <input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={address.city}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={address.country}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="postalCode"
                        placeholder="Postal Code"
                        value={address.postalCode}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="tel"
                        name="phoneNumber"
                        placeholder="Phone Number"
                        value={address.phoneNumber}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="order-summary">
                    <h3>Order Summary</h3>
                    <p>
                        <strong>Pickup Date:</strong> {new Date(pickupDate).toLocaleDateString()}
                    </p>
                    <p>
                        <strong>Return Date:</strong> {new Date(returnDate).toLocaleDateString()}
                    </p>
                    <ul>
                        {gowns.map((gown, index) => (
                            <li key={index}>
                                <strong>{gown.name}</strong> - ${gown.price} x {gown.quantity} pcs
                            </li>
                        ))}
                    </ul>
                    <p>
                        <strong>Total Cost:</strong> $
                        {gowns.reduce((total, gown) => total + gown.price * gown.quantity, 0)}
                    </p>
                </div>
                <button type="submit" className="proceed-button" disabled={loading}>
                    {loading ? 'Processing...' : 'Proceed to Payment'}
                </button>
            </form>
        </div>
    );
};

export default OrderForm;
