import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    const location = useLocation(); // Retrieve the location object to get the previous path

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            if (response.data.token) {
                // Store the token in localStorage if needed
                localStorage.setItem('token', response.data.token);

                // Redirect to the original path (e.g., /checkout) or default to /collection
                const redirectPath = location.state?.from || '/collection';
                navigate(redirectPath);
            }
        } catch (error) {
            setErrorMessage('Invalid login credentials. Please try again.');
        }
    };

    return (
        <div className="login-container">
            <h2>Sign-in</h2>
            <form onSubmit={handleLogin}>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                <label>Email</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <label>Password</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                <button type="submit">Log in</button>
            </form>
            <button 
                onClick={() => navigate('/signup', { state: { from: location.state?.from || '/collection' } })}
            >
                Sign up
            </button>
            <a href="/reset-password">Forgot your password?</a>
        </div>
    );
}

export default Login;
