import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "../utils/api";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { cartItems, totalPrice } = location.state || {};

  const [formData, setFormData] = useState({
    addressLine1: "",
    addressLine2: "",
    city: "",
    postcode: "",
    country: "",
    phoneNumber: "",
  });
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePlaceOrder = async () => {
    try {
      setLoading(true);

      const token = localStorage.getItem("token");
      if (!token) {
        alert("You must log in to place an order.");
        navigate("/login");
        return;
      }

      // Create the order
      const orderResponse = await axios.post(
        "/orders",
        { cartItems, totalPrice, shippingDetails: formData },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const { checkoutUrl } = orderResponse.data;
      alert("Order placed successfully! Redirecting to payment.");
      window.location.href = checkoutUrl; // Redirect to PayMongo checkout
    } catch (error) {
      console.error("Error placing order or initiating payment:", error.response?.data || error.message);
      alert("Failed to place order. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  if (!cartItems || cartItems.length === 0) {
    return <p>No items in your cart to checkout.</p>;
  }

  return (
    <div>
      <h1>Checkout</h1>
      <div>
        <h2>Shipping Details</h2>
        <form>
          <input
            type="text"
            name="addressLine1"
            placeholder="Address Line 1"
            value={formData.addressLine1}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="addressLine2"
            placeholder="Address Line 2"
            value={formData.addressLine2}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={formData.city}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="postcode"
            placeholder="Postcode"
            value={formData.postcode}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="country"
            placeholder="Country"
            value={formData.country}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="phoneNumber"
            placeholder="Phone Number"
            value={formData.phoneNumber}
            onChange={handleInputChange}
            required
          />
        </form>
      </div>
      <div>
        <h2>Order Summary</h2>
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              Gown: {item.gownId}, Price: ₱{item.price}, Start Date:{" "}
              {new Date(item.startDate).toLocaleDateString()}, End Date:{" "}
              {new Date(item.endDate).toLocaleDateString()}
            </li>
          ))}
        </ul>
        <h3>Total Price: ₱{totalPrice}</h3>
      </div>
      <button onClick={handlePlaceOrder} disabled={loading}>
        {loading ? "Processing..." : "Place Order"}
      </button>
    </div>
  );
};

export default Checkout;
