import React, { useState, useEffect } from "react";
import { fetchOrders, deleteOrder } from "../services/api";

const AdminBookings = () => {
  const [orders, setOrders] = useState([]);
  const [filter, setFilter] = useState(""); // Default: show all

  useEffect(() => {
    const loadOrders = async () => {
      try {
        const ordersData = await fetchOrders(filter);
        setOrders(ordersData);
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };

    loadOrders();
  }, [filter]);

  const handleDelete = async (orderId) => {
    try {
      await deleteOrder(orderId);
      setOrders(orders.filter((order) => order._id !== orderId));
    } catch (error) {
      console.error("Error deleting order:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Bookings Management</h1>
      <div className="mb-4">
        <button onClick={() => setFilter("")} className="btn btn-primary">All</button>
        <button onClick={() => setFilter("Pending")} className="btn btn-warning">Pending</button>
        <button onClick={() => setFilter("Completed")} className="btn btn-success">Completed</button>
      </div>
      <table className="min-w-full bg-white border">
        <thead>
          <tr>
            <th className="px-4 py-2 border">Booking ID</th>
            <th className="px-4 py-2 border">Client Email</th>
            <th className="px-4 py-2 border">Total Price</th>
            <th className="px-4 py-2 border">Status</th>
            <th className="px-4 py-2 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td className="px-4 py-2 border">{order._id}</td>
              <td className="px-4 py-2 border">{order.shippingDetails.email}</td>
              <td className="px-4 py-2 border">â‚±{order.totalPrice}</td>
              <td className="px-4 py-2 border">{order.status}</td>
              <td className="px-4 py-2 border">
                <button
                  onClick={() => handleDelete(order._id)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminBookings;
