import React, { useState } from 'react';
import Modal from 'react-modal';
import DatePicker from '../components/DatePicker';
import CalendarModal from '../components/CalendarModal';
import GownDetailsModal from '../components/GownDetailsModal';
import MyBookingSidebar from '../components/MyBookingSidebar';
import './Collection.css';

const dresses = [
    { id: 1, name: 'Pink Gown', price: 'P.1000', days: '4 days', image: 'pinkgown.jpg', description: 'Antique rose full-length elegant gown...' },
    { id: 2, name: 'Blue Gown', price: 'P.1000', days: '4 days', image: 'bluegown.jpg', description: 'Sparkling dress for special occasions...' },
    // Add more dress items here
];

Modal.setAppElement('#root');

function Collection() {
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [calendarOpen, setCalendarOpen] = useState(false);
    const [selectedDress, setSelectedDress] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [dateRange, setDateRange] = useState({
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
    });
    const [cartItems, setCartItems] = useState([]);

    const openModal = (dress) => {
        setSelectedDress(dress);
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
        setSelectedDress(null);
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleDateChange = (range) => {
        setDateRange(range.selection);
        setCalendarOpen(false);  // Close calendar after date selection
    };

    const handleAddToCart = (gown, quantity) => {
        setCartItems([...cartItems, { ...gown, quantity }]);
        setModalIsOpen(false);  // Close gown details modal after adding to cart
    };

    const filteredDresses = dresses.filter((dress) => {
        return dress.name.toLowerCase().includes(searchQuery.toLowerCase());
    });

    return (
        <div className="collection-page">
            <header className="collection-banner">
                <h1 className="collection-title">The Collection</h1>
            </header>
            <div className="collection-content">
                <h2 className="collection-subtitle">Dresses for every event</h2>
                <div className="filter-search">
                    <button onClick={() => setCalendarOpen(true)} className="date-button">Select Rental Dates</button>
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={handleSearchChange}
                        placeholder="Search products"
                        className="search-input"
                    />
                </div>
                <div className="dress-grid">
                    {filteredDresses.map((dress) => (
                        <div key={dress.id} className="dress-card" onClick={() => openModal(dress)}>
                            <img src={require(`../assets/${dress.image}`)} alt={dress.name} className="dress-image" />
                            <div className="dress-info">
                                <h3>{dress.name}</h3>
                                <p>{dress.days} {dress.price}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Calendar Modal for Date Selection */}
            {calendarOpen && (
                <CalendarModal
                    dateRange={dateRange}
                    onDateChange={handleDateChange}
                    onClose={() => setCalendarOpen(false)}
                />
            )}

            {/* Gown Details Modal */}
            {selectedDress && (
                <GownDetailsModal
                    gown={selectedDress}
                    onClose={closeModal}
                    onAddToCart={handleAddToCart}
                />
            )}

            {/* My Booking Sidebar */}
            <MyBookingSidebar cartItems={cartItems} />
        </div>
    );
}

export default Collection;
