import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
    const location = useLocation();

    // Sample cart items, this could be fetched or managed globally (like in Context or Redux)
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        // Simulate fetching cart items from a global store or localStorage
        const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
        setCartItems(storedCartItems);
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);

                // Navigate to order-form with cart items
                navigate('/order-form', { state: { cartItems } });
            }
        } catch (error) {
            setErrorMessage('Invalid login credentials. Please try again.');
        }
    };

    return (
        <div className="login-container">
            <h2>Sign-in</h2>
            <form onSubmit={handleLogin}>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                <label>Email</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <label>Password</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                <button type="submit">Log in</button>
            </form>
            <button onClick={() => navigate('/signup')}>Sign up</button>
            <a href="/reset-password">Forgot your password?</a>
        </div>
    );
}

export default Login;
