import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Cart.css";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const navigate = useNavigate();

  // Fetch cart items
  useEffect(() => {
    const fetchCart = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        console.warn("No token found, user is not logged in.");
        return;
      }

      try {
        const response = await fetch("http://localhost:5000/api/cart", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const data = await response.json();
        console.log("Fetched Cart Items:", data.cartItems);
        console.log("Total Price:", data.totalPrice);

        if (!response.ok) {
          throw new Error(data.message || "Failed to fetch cart data");
        }

        setCartItems(data.cartItems || []);
        setTotalPrice(data.totalPrice || 0);
      } catch (error) {
        console.error("Error fetching cart:", error.message);
      }
    };

    fetchCart();
  }, []);

  const handleRemove = async (itemIndex) => {
    const updatedCart = [...cartItems];
    updatedCart.splice(itemIndex, 1);
    setCartItems(updatedCart);

    const updatedTotal = updatedCart.reduce((sum, item) => sum + item.price, 0);
    setTotalPrice(updatedTotal);

    // You can add a backend call here to update the cart in the database if required
  };

  const handleCheckout = () => {
    alert("Proceeding to checkout...");
    navigate("/checkout");
  };

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      {cartItems.length > 0 ? (
        <div>
          <ul className="cart-list">
            {cartItems.map((item, index) => (
              <li key={index} className="cart-item">
                <div className="cart-details">
                  <h2>Gown: {item.gownId}</h2>
                  <p>Price: ₱{item.price}</p>
                  <p>
                    Rental Dates: {new Date(item.startDate).toLocaleDateString()} -{" "}
                    {new Date(item.endDate).toLocaleDateString()}
                  </p>
                  <button onClick={() => handleRemove(index)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
          <h2>Total Price: ₱{totalPrice}</h2>
          <button className="checkout-button" onClick={handleCheckout}>
            Proceed to Checkout
          </button>
        </div>
      ) : (
        <p>Your cart is empty!</p>
      )}
    </div>
  );
};

export default Cart;
